import heroImg from './hero-img.jpg';
import './Hero.css';

export default function Hero() {
	return(
		<section>
			<div className="p-4 flex justify-between">
				<div className="w-[62%]">
					<h1 className="mb-8 text-[3rem]">Tired of tracking your students' record on paper? It's time to go digital</h1>
					<p className="hero__subtext mb-8 text-[1.25rem]">Manage student records, administer tests and track attendance with our student management system</p>
					<a href="/signup" className="inline-block mr-4 p-3 rounded-lg bg-violet-700 text-violet-100">Sign Up</a>
					<a href="/demo" className="inline-block p-3 rounded-lg bg-violet-100 text-violet-900">Try it out</a>
				</div>
				<img src={heroImg} className="w-[32%] self-start"/>
			</div>
		</section>
	);
}
