import './App.css';
import Navbar from './components/Navbar';
import Hero from './components/Hero'
import { useState, useEffect } from 'react';

function App() {
  const [isFlagEnabled, setIsFlagEnabled] = useState(false);
  const [email, setEmail] = useState('');

  async function fetchFlagValue() {
    try {
        const response = await fetch('http://localhost:3000/api/v1/flag');
        response.json().then((data) => {
          setIsFlagEnabled(data);
        });
      } catch(err) {
        console.log(err);
      }
  }

  useEffect(() => {
    if(!isFlagEnabled) {
      fetchFlagValue();
    }

    let interval = setInterval(fetchFlagValue, 1000 * 60);

    return () => {
      clearInterval(interval);
    };

  }, [isFlagEnabled]);

  function submitForm(event) {
    // LOOK AT ME document.get FOrm name - email thingy
    event.preventDefault();
    const formData = new FormData();
    formData.append('email', email);

    try {
      fetch('http://localhost:3000/api/v1/send', {
        method: 'POST',
        body: JSON.stringify(email)
      });
    } catch(err) {
      console.log(err);
    }
  }
  return (
    <div className="App">
      <Navbar/>
      <Hero/>
      <section className="container py-8">
        <h2 className="text-2xl text-violet-900 font-bold">Store student grades in the cloud</h2>
        <p className="text-lg w-[66%]">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
    tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,
    quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
    consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse
    cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non
    proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </p>
      </section>
      <section className="container py-4">
        <div className="p-2 ">
          <h3 className="text-xl mb-4 font-bold">{isFlagEnabled ? "Get Access to Exclusive Education Content" : "Subscribe to Our Newsletter"}</h3>
          <p className="mb-3 w-[70%]  border border-1 rounded">We bring you the latest research on effective learning methods every week, you also get access to download FREE ebooks on blended learning. Subscribe to our newsletter below!</p>
          <form onSubmit={submitForm} className="w-[50%]">
            <div>
            <label className="font-semibold" name="email">Email</label>
            </div>
            <input type="email" placeholder="name@example.com" onChange={ (event) => setEmail(event.target.value) } className="mr-2 p-1 rounded outline outline-1"/>
            <input type="submit" value="Subscribe" className="p-1.5 rounded bg-violet-500 text-white"/>
          </form>
        </div>
      </section>
    </div>
  );
}

export default App;
