import heroImg from './carlos-muza-hpjSkU2UYSU-unsplash.jpg';

export default function Hero() {
	function hello() {
		// Change this to make the API call
		alert('hello');
	}

	return(
		<div className="bg-violet-200 pb-8">
			<section className="container">
				<div className="pt-[2rem] flex justify-between">
					<div className="w-[62%]">
						<h1 className="mb-8 text-[3rem] text-violet-900 text-left">
							Tired of tracking your students' records on paper? It's time to go digital
						</h1>
						<p className="mb-8 text-[1.25rem] text-left">
							Manage student records, administer tests and track attendance with our <span className="text-indigo-900">student management system</span>
						</p>
						<div className="flex items-start">
							<a href="/signup" className="inline-block mr-4 p-3 rounded-lg bg-violet-700 text-violet-100">Sign Up</a>
							<a href="/demo" onClick={hello} className="inline-block max-w-[100%] p-3 rounded-lg outline bg-violet-100 text-violet-900">Try it out</a>
						</div>
					</div>
					<img src={heroImg} alt="Student grades" />
				</div>
			</section>
		</div>
	);
}
