import * as React from 'react';
import axios from 'axios';

export default function SearchAppBar({ isLoggedIn }) {
    const [videos, setVideos] = React.useState("");
    const [cover, setCover] = React.useState("");
    const [title, setTitle] = React.useState("");

    const submitForm = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("title", title);
        formData.append("video", video);
        formData.append("cover", cover);

        const token = localStorage.getItem('token');
        await axios.post("http://localhost:3002/api/v1/video", formData, {
            headers: ({
                Authorization: 'Bearer' + token
            })
        });
    }

    return (
        <div>
            <form noValidate onSubmit={submitForm}>
                <label>Video Title:</label>
                <input type="text" id="title" name="title" onChange={(e) => setTitle(e.target.value)} autoFocus required/>
                
                <label>Select Video:</label>
                <input type="file" id="video" name="video" onChange={(e) => setVideos(e.target.files[0])} required/>

                 <label>Select Cover Image:</label>
                 <input type="file" id="coverImage" name="coverImage" onChange={(e) => setCover(e.target.files[0])} required/>

                 <button type="submit">Upload</button>
            </form>
        </div>
    );
}
