export default function Hero() {
	return(
		<section>
			<div className="max-w-[80%] flex justify-between">
				<div className="w-[62%]">
					<h1 className="text-[3rem]">Tired of tracking your students' record on paper? It's time to go digital</h1>
					<p className="text-[1.5rem]">Manage student records, administer tests and track attendance with our student management system</p>
				</div>
			</div>
		</section>
	);
}
